-- ----------------------------
-- Table structure for OT_ad
-- ----------------------------
DROP TABLE IF EXISTS `OT_ad`;
CREATE TABLE `OT_ad` (
  `AD_ID` int(11) NOT NULL AUTO_INCREMENT,
  `AD_type` varchar(25) DEFAULT NULL,
  `AD_num` smallint(6) NOT NULL DEFAULT '0',
  `AD_theme` varchar(80) NOT NULL,
  `AD_code` longtext,
  `AD_upImgStr` text,
  `AD_divStyle` varchar(255) DEFAULT NULL,
  `AD_width` smallint(6) NOT NULL DEFAULT '0',
  `AD_height` smallint(6) NOT NULL DEFAULT '0',
  `AD_isTime` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `AD_startTime` datetime DEFAULT NULL,
  `AD_endTime` datetime DEFAULT NULL,
  `AD_price` decimal(10,2) NOT NULL DEFAULT '0.00',
  `AD_rank` int(6) NOT NULL DEFAULT '0',
  `AD_isLock` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `AD_isRev` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `AD_state` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `AD_areaStr` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`AD_ID`),
  KEY `AD_num` (`AD_num`)
) ENGINE=MyISAM AUTO_INCREMENT=70 DEFAULT CHARSET=utf8;

