INSERT INTO `OT_info` VALUES ('4102', '2012-10-02 23:46:42', '2014-05-31 21:01:30', 'news', '', '', '', ',11,48,', '11', '48', '0', '新闻发布系统', '网钛科技', '0', '', '【最全】网钛CMS视频教程', '72ce4d1088fa6ea7e06dd6df4e181e9e', 'font-weight:bold;color:#ff0000;', '', '0', '最全,视频,教程', '6861,6860,6859,6707,6709,6165,4973,4975', '4976', '4101', '网钛文章管理系统视频教程系列第一讲：LOGO栏位的设置第二讲：导航菜单的设置第三讲：滚动信息的设置第四讲：首页幻灯的设置第五讲：网站公告的设置第六讲：首页最新消息的设置第七讲：首页精彩推荐的设置第八讲：滚动图片的设置第九讲：热门文章的设置第十讲：调查投...', '<p style=\"text-align:center;margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;font-size:36pt;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">网钛文章管理系统</span></b><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-size:36pt;\" lang=\"EN-US\"><!--?xml:namespace prefix = o /--><o:p></o:p></span></b></p>\r\n<p style=\"text-align:center;margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-family:宋体;font-size:36pt;mso-ascii-font-family:Calibri;mso-hansi-font-family:Calibri;\">视频教程系列</span></b><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-size:36pt;\" lang=\"EN-US\"><o:p></o:p></span></b></p>\r\n<p style=\"text-align:center;margin:0cm 0cm 0pt;\" class=\"MsoNormal\" align=\"center\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-size:14pt;\" lang=\"EN-US\"><o:p></o:p></span></b><br />\r\n&nbsp;</p>\r\n<o:p> <div><span style=\"font-family:Calibri;\"><b style=\"mso-bidi-font-weight:normal;\"><span style=\"font-size:14pt;\" lang=\"EN-US\">百度网盘<span style=\"font-size:18px;\">下载：</span></span></b></span><span style=\"color:#444444;font-family:Calibri;\"><span style=\"font-size:12px;line-height:16px;\"><b><a href=\"http://pan.baidu.com/s/1cVZe2\" target=\"_blank\"><span style=\"font-size:18px;\">http://pan.baidu.com/s/1cVZe2</span></a></b></span></span></div>\r\n<p style=\"text-align:center;margin:0cm 0cm 0pt;\" class=\"MsoNormal\"