INSERT INTO `OT_infoWeb` VALUES ('4', 'news', '2011-02-09 11:09:50', '2015-12-19 16:15:07', '2015-12-19 16:15:07', 'web', '禁止转载', '', '0', '', '', '', '', '', '', '_blank', '', '0', '<strong>出处：<span style=\"color:#0000ff;\">网钛科技</span><br />\r\n网址：</strong><a href=\"http://otcms.com/\"><strong><span style=\"font-size:16px;\">http://otcms.com</span></strong></a><br />\r\n<strong><span style=\"color:#ff0000;\">转载时请注明出处和网址</span><br />\r\n<br />\r\n目前网钛科技有限公司的产品如下：<br />\r\n<span style=\"font-size:18px;\">1、<a href=\"http://otcms.com\" target=\"_blank\"><span style=\"font-size:18px;\">网钛文章CMS</span></a>：</span><a href=\"http://otcms.com/\" target=\"_blank\"><span style=\"font-size:18px;\">http://otcms.com</span></a><br />\r\n<span style=\"font-size:18px;\">2、</span><span style=\"font-size:18px;\"><span style=\"font-size:18px;\"><span style=\"font-size:18px;\">网钛</span>互联</span></span><span style=\"font-size:18px;\">：</span><a href=\"http://otcms.net/\" target=\"_blank\"><span style=\"font-size:18px;\">http://www.otcms.net</span></a><br />\r\n</strong> <p>\r\n	<strong><span style=\"font-size:18px;\">3、<a href=\"http://www.v0000.com\" target=\"_blank\"><span style=\"font-size:18px;\">网钛VPS教程网</span></a></span><span style=\"font-size:18px;\">：</span><a href=\"http://www.v0000.com/\" target=\"_blank\"><span style=\"font-size:18px;\">http://www.v0000.com</span></a></strong> </p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;line-height:2;\">4、</span></strong><b><span style=\"font-size:16px;\"><a href=\"http://otcms.com/news/7779.html\" target=\"_blank\"><span style=\"font-size:16px;line-height:2;\">网钛远程桌面管理助手</span></a></span></b> </p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;line-height:2;\">5、</span></strong><span style=\"font-size:medium;\"><span style=\"line-height:25px;\"><b><a href=\"http://otcms.com/news/7780.html\" target=\"_blank\"><span style=\"font-size:18px;line-height:2;\">网钛FTP数据备份助手</span></a></b></span></span> </p>\r\n<p>\r\n	</p>\r\n<p><span style=\"color:#ff0000;\"><b><span style=\"font-size:16px;\"><br />\r\n</span></b></span></p>\r\n<p><span style=\"color:#ff0000;\"><b><span style=\"font-size:16px;\">企业客服QQ:800166366</span></b></span> <!-- WPA Button Begin -->\r\n<script charset=\"utf-8\" type=\"text/javascript\" src=\"http://wpa.b.qq.com/cgi/wpa.php?key=XzgwMDE2NjM2Nl8zNTUwNDBfODAwMTY2MzY2Xw\"></script>\r\n<!-- WPA Button End --></p>', '|', '', '1', '0', '140', '1', '0', '', '', null, '1');
