-- ----------------------------
-- Table structure for OT_menuTree
-- ----------------------------
DROP TABLE IF EXISTS `OT_menuTree`;
CREATE TABLE `OT_menuTree` (
  `MT_ID` int(11) NOT NULL AUTO_INCREMENT,
  `MT_level` int(11) NOT NULL DEFAULT '0',
  `MT_menuID` int(11) NOT NULL DEFAULT '0',
  `MT_appID` int(11) NOT NULL DEFAULT '0',
  `MT_fileID` int(11) NOT NULL DEFAULT '0',
  `MT_theme` varchar(50) NOT NULL,
  `MT_fileName` varchar(25) DEFAULT NULL,
  `MT_getMudi` varchar(20) DEFAULT NULL,
  `MT_getDataMode` varchar(50) DEFAULT NULL,
  `MT_getDataModeStr` varchar(50) DEFAULT NULL,
  `MT_getDataType` varchar(20) DEFAULT NULL,
  `MT_getDataTypeCN` varchar(50) DEFAULT NULL,
  `MT_getDataType2` varchar(20) DEFAULT NULL,
  `MT_getDataID` int(11) NOT NULL DEFAULT '0',
  `MT_getImgStr` varchar(255) DEFAULT NULL,
  `MT_getOthers` varchar(150) DEFAULT NULL,
  `MT_childMenu` varchar(255) DEFAULT NULL,
  `MT_URL` text,
  `MT_isFastMenu` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `MT_userState` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `MT_rank` int(11) NOT NULL DEFAULT '0',
  `MT_state` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `MT_themeStyle` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`MT_ID`),
  KEY `MT_rank` (`MT_rank`),
  KEY `MT_level` (`MT_level`) USING BTREE,
  KEY `MT_state` (`MT_state`),
  KEY `MT_menuID` (`MT_menuID`),
  KEY `MT_appID` (`MT_appID`),
  KEY `MT_userState` (`MT_userState`)
) ENGINE=MyISAM AUTO_INCREMENT=915 DEFAULT CHARSET=utf8;

