-- Table structure for OT_message
-- ----------------------------
DROP TABLE IF EXISTS `OT_message`;
CREATE TABLE `OT_message` (
  `MA_ID` int(11) NOT NULL AUTO_INCREMENT,
  `MA_type` varchar(30) DEFAULT NULL,
  `MA_time` datetime NOT NULL,
  `MA_ip` varchar(20) DEFAULT NULL,
  `MA_ipCN` varchar(80) DEFAULT NULL,
  `MA_num` int(11) NOT NULL DEFAULT '0',
  `MA_userID` int(11) NOT NULL DEFAULT '0',
  `MA_username` varchar(50) DEFAULT NULL,
  `MA_content` text,
  `MA_contentMd5` varchar(32) DEFAULT NULL,
  `MA_replyTime` datetime DEFAULT NULL,
  `MA_reply` text,
  `MA_isReply` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `MA_state` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `MA_addiContent` text,
  `MA_vote1` int(11) DEFAULT '0',
  `MA_vote2` int(11) DEFAULT '0',
  PRIMARY KEY (`MA_ID`),
  KEY `MA_time` (`MA_time`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

